package com.titiredennis.seminar2;// Vector int// Parcurgere si afisare care este impar si parpublic class ParImpar {    public static void main(String[] args) {        int [] vector = {2,3,4,5,6,7,8};        for (int i=0; i<=vector.length; i++) {            if (vector[i] % 2 == 0) {                System.out.println("Numarul par este: " + vector[i]);            } else {                System.out.println("Numarul par este: " + vector[i]);            }        }    }}