package com.titiredennis.seminar2;/* factorial folosinr recursivitate n=3 pas1: int nr = 3;       apelam factorial cu nr-1 */public class RecursivFactorial {    public static void main(String[] args) {        int n = 3;        System.out.println(factorial(n));    }    static int factorial(int nr) {        if (nr > 1) {            return nr * factorial(nr - 1);        } else {            return 1;        }    }}